$(document).ready(function () {

    $(".pg-detail img").each(function (index) {
        var src = $(this).attr('src');
        $(this).wrap('<a class="image-link" href="' + src + '"></a>');
        popup_show_img();
    });
});
function popup_show_img() {
    $('.image-link').magnificPopup({
        type: 'image'
    });
}